const express = require('express');
const bodyParser = require('body-parser');
const compression = require('compression');

const { graphqlHTTP } = require('express-graphql');
const { schema } = require('./graphql');

const { logger } = require('./utils/logger');
const { reqInfo } = require('./middleware/reqInfo');
const author = require('./routes/author');
const book = require('./routes/book');
const genre = require('./routes/genre');
const bookInstance = require('./routes/bookInstance');

const app = express();
app.use(bodyParser.json());
app.use(compression());

// å¼•ç”¨å…¨å±€ä¸­é—´ä»¶
app.use(reqInfo);

// æˆ‘ä»¬å®šä¹‰ä¸€ä¸ªè·¯ç”±å¯¹User å’Œ Book åšè·¯ç”±çš„CRUD
app.use('/author', author);
app.use('/book', book);
app.use('/genre', genre);
app.use('/book-instance', bookInstance);

// ä¸‹é¢æ˜¯å•ç‹¬çš„graphQL è·¯ç”±
app.use(
  '/graphql',
  graphqlHTTP({
    schema,
  }),
);

// è®¾ç½®ä¸€ä¸ªè·¯ç”±ï¼Œå¦‚æžœå‰é¢çš„éƒ½æœ‰é—®é¢˜ï¼Œå°±åˆ°è¿™é‡Œæ¥å¤„ç†é”™è¯¯
app.use((err, req, res, next) => {
  logger.error({
    level: 'error',
    message: err.message,
  });
  res.status(500).json(err);
});
module.exports = app;

